mapper.select:
select case_id from test_case

    project_id = #{projectId}
    and
    folder_id in

     #{folderId}

    and case_id not in

        #{caseId}

select count(*) from test_case

    project_id = #{projectId}

       and
       case_id  in

           #{caseId}

SELECT * FROM ( SELECT tc.case_id, tc.summary, tc.folder_id, tc.project_id, CONCAT_WS( '-', tpi.project_code, tc.case_num ) AS case_num, tc.priority_id, tp.name AS priority_name, tp.colour AS priority_colour, tp.sequence, tc.created_by, tc.last_updated_by, tc.creation_date, tc.last_update_date, tc.object_version_number FROM test_case tc JOIN test_project_info tpi on tpi.project_id = tc.project_id JOIN test_priority tp ON tc.priority_id = tp.id WHERE tc.project_id = #{projectId} AND tc.folder_id IN
#{folderId}
) as tci
AND tci.priority_id = #{searchDTO.searchArgs.priorityId}

select count(tc.case_id)
from test_case tc
where tc.priority_id = #{priorityId}
AND tc.project_id IN

    #{index}

