SELECT cycle.execute_id, cycle.cycle_id, cycle.case_id, cycle.rank, cycle.object_version_number, cycle.execution_status, cycle.assigned_to, cycle.description, tc.cycle_name, attach.url, attach.attachment_name, attach.id, cycle.last_update_date, cycle.last_updated_by, cycle.creation_date, cycle.created_by, defect.defect_type, defect.id defece_id, defect.defect_link_id, (select execute_id from test_cycle_case where cycle_id=cycle.cycle_id and cycle.rank > rank order by rank DESC limit 1) last_execute_id, (select execute_id from test_cycle_case where cycle_id=cycle.cycle_id and cycle.rank < rank order by rank ASC limit 1) next_execute_id, defect.issue_id defect_issue_id FROM ( SELECT * FROM test_cycle_case
AND issue_id = #{dto.caseId}
AND cycle_id = #{dto.cycleId}
AND execute_id = #{dto.executeId}
AND project_id = #{dto.projectId}
AND execution_status = #{dto.executionStatus}
AND description like CONCAT(CONCAT('%', #{dto.description}), '%')
AND assigned_to = #{dto.assignedTo}
AND last_updated_by = #{dto.lastUpdatedBy}
AND last_update_date >= #{dto.lastUpdateDate}
order by rank
limit #{page} , #{pageSize}
) cycle LEFT JOIN test_cycle_case_attach_rel attach ON cycle.execute_id = attach.attachment_link_id AND attach.attachment_type = 'CYCLE_CASE' LEFT JOIN test_cycle_case_defect_rel defect ON cycle.execute_id = defect.defect_link_id AND defect.defect_type = 'CYCLE_CASE' left join test_cycle tc on cycle.cycle_id = tc.cycle_id

