mapper.select:
SELECT tp.plan_id, tp.name plan_name, tcc.execute_id, tcc.cycle_id, tcc.case_id, tcc.rank, ts.status_name execution_status_name, ts.status_color, tcc.execution_status, tcc.summary, tcc.last_updated_by FROM test_cycle_case tcc LEFT JOIN test_cycle tc ON tcc.cycle_id = tc.cycle_id LEFT JOIN test_plan tp on tc.plan_id = tp.plan_id LEFT JOIN test_status ts ON tcc.execution_status = ts.status_id WHERE tcc.case_id IN
#{caseId}
AND tp.sprint_id = #{sprintId} ORDER BY tcc.rank

SELECT
 tcc.execution_status
FROM test_cycle_case tcc
JOIN (
   select cycle_id from test_cycle tc1
   where
   tc1.project_id = #{projectId}
   AND tc1.plan_id in (
      select plan_id from test_plan tp
      where tp.project_id = #{projectId}
      and tp.sprint_id = #{sprintId}
   )
) tc ON tc.cycle_id = tcc.cycle_id
JOIN test_case_link tcl on tcl.project_id = #{projectId} and tcl.issue_id = #{issueId} and  tcl.link_case_id = tcc.case_id
WHERE
 tcc.project_id = #{projectId}

