SELECT cycle.execute_id, cycle.cycle_id, cycle.issue_id, cycle.rank, cycle.object_version_number, cycle.execution_status, cycle.assigned_to, cycle.description, attach.url, attach.attachment_name, attach.id, cycle.last_update_date, cycle.last_updated_by, defect.defect_type, defect.id defece_id, defect.defect_link_id, defect.issue_id defect_issue_id FROM ( SELECT * FROM ( SELECT tt.*, ROWNUM AS rowno FROM ( SELECT * FROM test_cycle_case
AND issue_id = #{dto.issueId}
AND cycle_id = #{dto.cycleId}
AND execute_id = #{dto.executeId}
AND execution_status = #{dto.executionStatus}
AND description like CONCAT(CONCAT('%', #{dto.comment}), '%')
AND assigned_to = #{dto.assignedTo}
AND last_updated_by = #{dto.lastUpdatedBy}
AND last_update_date >= #{dto.lastUpdateDate}
ORDER BY RANK ) tt
WHERE ROWNUM <= #{pageSize}
) table_alias
WHERE table_alias.rowno >= #{page}
) cycle LEFT JOIN test_cycle_case_attach_rel attach ON cycle.execute_id = attach.attachment_link_id AND attach.attachment_type = 'CYCLE_CASE' LEFT JOIN test_cycle_case_defect_rel defect ON cycle.execute_id = defect.defect_link_id AND defect.defect_type = 'CYCLE_CASE'

SELECT cycle.execute_id, cycle.cycle_id, cycle.issue_id, cycle.rank, cycle.object_version_number, cycle.execution_status, cycle.assigned_to, cycle.description, attach.url, attach.attachment_name, attach.id, cycle.last_update_date, cycle.last_updated_by, defect.defect_type, defect.id defece_id, defect.defect_link_id, defect.issue_id defect_issue_id FROM ( SELECT * FROM test_cycle_case
AND issue_id = #{dto.issueId}
AND cycle_id = #{dto.cycleId}
AND execute_id = #{dto.executeId}
AND execution_status = #{dto.executionStatus}
AND description like CONCAT(CONCAT('%', #{dto.comment}), '%')
AND assigned_to = #{dto.assignedTo}
AND last_updated_by = #{dto.lastUpdatedBy}
AND last_update_date >= #{dto.lastUpdateDate}
order by rank
limit #{page} , #{pageSize}
) cycle LEFT JOIN test_cycle_case_attach_rel attach ON cycle.execute_id = attach.attachment_link_id AND attach.attachment_type = 'CYCLE_CASE' LEFT JOIN test_cycle_case_defect_rel defect ON cycle.execute_id = defect.defect_link_id AND defect.defect_type = 'CYCLE_CASE'

