cycle_id in
#{dto.cycleId}
AND assigned_to = #{dto.assignedTo}
AND last_updated_by = #{dto.lastUpdatedBy}
order by rank
limit #{page} , #{pageSize}
) cycle LEFT JOIN test_cycle_case_attach_rel attach ON cycle.execute_id = attach.attachment_link_id AND attach.attachment_type = 'CYCLE_CASE' LEFT JOIN test_cycle_case_defect_rel defect ON cycle.execute_id = defect.defect_link_id AND defect.defect_type = 'CYCLE_CASE' LEFT JOIN test_cycle rCycle ON cycle.cycle_id = rCycle.cycle_id

