SELECT cycle.execute_id, cycle.cycle_id, cycle.issue_id, cycle.rank, cycle.object_version_number, cycle.execution_status, cycle.assigned_to, cycle.description, cycle.last_update_date, cycle.last_updated_by, cycle1.version_id, status1.status_name, step.test_step, step.test_data, step.expected_result, step.step_coment, step.step_status_name, defect.defect_type, defect.id defece_id, defect.defect_link_id, defect.issue_id defect_issue_id, attach.url, attach.attachment_name, attach.id, attach.attachment_type, attach.attachment_link_id, CASE cycle1.type WHEN 'folder' THEN ( SELECT cycle2.cycle_name FROM test_cycle cycle2 WHERE cycle1.parent_cycle_id = cycle2.cycle_id ) ELSE cycle1.cycle_name END AS cycle_name, CASE cycle1.type WHEN 'folder' THEN cycle1.cycle_name END AS folder_name FROM test_cycle_case cycle LEFT JOIN test_cycle cycle1 ON cycle1.cycle_id = cycle.cycle_id LEFT JOIN test_status status1 ON cycle.execution_status = status1.status_id AND status1.status_type = 'CYCLE_CASE' LEFT JOIN ( SELECT step1.execute_step_id, step1.execute_id, step1.step_id, step1.description step_coment, status_name step_status_name, step2.test_step, step2.test_data, step2.expected_result FROM test_cycle_case_step step1 LEFT JOIN test_case_step step2 ON step1.step_id = step2.step_id LEFT JOIN test_status ON step1.step_status = status_id AND status_type = 'CASE_STEP' ORDER BY rank ) step ON step.execute_id = cycle.execute_id LEFT JOIN test_cycle_case_defect_rel defect ON ( cycle.execute_id = defect.defect_link_id AND defect.defect_type = 'CYCLE_CASE' ) OR ( step.execute_step_id = defect.defect_link_id AND defect.defect_type = 'CASE_STEP' ) LEFT JOIN test_cycle_case_attach_rel attach ON ( cycle.execute_id = attach.attachment_link_id AND attach.attachment_type = 'CYCLE_CASE' ) OR ( step.execute_step_id = attach.attachment_link_id AND attach.attachment_type = 'CYCLE_STEP' ) OR ( step.step_id = attach.attachment_link_id AND attach.attachment_type = 'CASE_STEP' )
cycle1.cycle_id in
#{item}
cycle1.version_id in
#{item}
ORDER BY cycle.rank

