mapper.select:
SELECT cycle1.cycle_id, cycle1.parent_cycle_id, cycle1.folder_id, cycle1.cycle_name, cycle1.type, cycle1.version_id, cycle1.description, cycle1.build, cycle1.environment, cycle1.from_date, cycle1.to_date, cycle1.object_version_number, cycle1.created_by, cycle1.rank, color1.projectId, color1.color, color1.counts, color1.statusName, color1.statusId FROM test_cycle cycle1 LEFT JOIN ( SELECT color, count( 1 ) counts, temp1.cycle_id, statusId, statusName, projectId FROM ( SELECT cycle.execute_id, cycle.cycle_id, status1.status_id statusId, status1.status_color color, status1.status_name statusName, status1.project_id projectId FROM test_cycle_case cycle LEFT JOIN test_status status1 ON cycle.execution_status = status1.status_id where cycle.project_id = #{projectId}
AND cycle.assigned_to = #{assignedTo}
) temp1 GROUP BY temp1.statusId, temp1.cycle_id ) color1 ON color1.cycle_id = cycle1.cycle_id WHERE cycle1.project_id = #{projectId} AND cycle1.version_id IN
#{item}
ORDER BY cycle1.rank, cycle1.cycle_id, color1.statusId

select count(cycle_id) from test_cycle

    cycle_name = #{cycleName} and version_id = #{versionId}


            and parent_cycle_id=#{parentCycleId}


            and type !='folder'



        and cycle_id !=#{cycleId}

SELECT cycle_id,cycle_name,rank FROM test_cycle
parent_cycle_id=#{dto.cycleId}
ORDER BY rank

SELECT cycle_id,cycle_name,rank FROM test_cycle
version_id = #{dto.versionId}
AND type = #{dto.type}
ORDER BY rank

SELECT cycle_id,cycle_name,rank FROM test_cycle WHERE cycle_id IN
#{item}
ORDER BY rank

mapper.update:
update test_cycle
set created_by = #{userId}, creation_date = #{date}, last_updated_by = #{userId}, last_update_date = #{date}
where cycle_id IN

    #{item}

mapper.select:
SELECT b.*,a.caseCount from test_cycle b, (SELECT tc.cycle_id, count(tcc.execute_id)as caseCount FROM test_cycle tc LEFT JOIN test_cycle_case tcc ON tcc.cycle_id = tc.cycle_id WHERE tc.project_id=#{projectId}
and tc.type = #{type}
and tc.plan_id in
#{planId}
GROUP BY tc.cycle_id) a where b.project_id = #{projectId} and a.cycle_id=b.cycle_id order by rank

mapper.update:
UPDATE test_cycle
SET plan_id = #{planId}
WHERE
    version_id = #{versionId}

update.foreach:
update test_cycle
set rank = #{cycle.rank}
where cycle_id = #{cycle.cycleId}
