mapper.select:
SELECT
id,project_id,action_type,source_type,object_version_number,
linked_id,file_url,file_stream,status,successful_count,failed_count,
created_by,creation_date,last_update_date
FROM test_fileload_history

    creation_date = (
    SELECT
    MAX(creation_date)
    FROM test_fileload_history

        created_by = #{createdBy} AND action_type = #{actionType}

    ) AND action_type = #{actionType}
    AND project_id = #{projectId}

SELECT tfh.id, tfh.project_id, tfh.action_type, tfh.source_type, tfh.object_version_number, tfh.linked_id, tfh.file_url, tfh.file_stream, tfh.status, tfh.successful_count, tfh.failed_count, tfh.created_by, tfh.creation_date, tfh.last_update_date, tif.name FROM test_fileload_history tfh, test_issue_folder tif WHERE tfh.linked_id = tif.folder_id
AND tif.name LIKE CONCAT(CONCAT('%',#{advancedSearchArgs.name, jdbcType=VARCHAR}),'%')
AND tfh.status = #{advancedSearchArgs.status}
AND tfh.linked_id IN
#{folderId}
