mapper.select:
SELECT *
from test_status

    project_id in(0,#{dto.projectId}) and status_type=#{dto.statusType}

        and status_name=#{dto.statusName}


        and description LIKE CONCAT(CONCAT('%', #{dto.description}), '%')

SELECT status_id, status_name, description, status_color, status_type, object_version_number, project_id FROM test_status WHERE 1=1 AND ( project_id = 0 OR project_id IN
#{projectId}
)
AND status_name LIKE CONCAT(CONCAT('%', #{statusName}), '%')
AND status_type= 'CYCLE_CASE'

mapper.update:
update test_status
set created_by = #{userId}, creation_date = #{date}, last_updated_by = #{userId}, last_update_date = #{date}
where status_id = #{statusId}
